package com.nihb.common.helper;

public class GlassesItem {
	NihbDecimal cost;
	String description;
	String nihbComments;
	public GlassesItem() {
		
		// TODO Auto-generated constructor stub
	}
	/**
	 * @return the cost
	 */
	public NihbDecimal getCost() {
		return cost;
	}
	/**
	 * @param cost the cost to set
	 */
	public void setCost(NihbDecimal cost) {
		this.cost = cost;
	}
	/**
	 * @return the description
	 */
	public String getDescription() {
		return description;
	}
	/**
	 * @param description the description to set
	 */
	public void setDescription(String description) {
		this.description = description;
	}
	/**
	 * @return the nihbComments
	 */
	public String getNihbComments() {
		return nihbComments;
	}
	/**
	 * @param nihbComments the nihbComments to set
	 */
	public void setNihbComments(String nihbComments) {
		this.nihbComments = nihbComments;
	}
	
	

}
